import streamlit as st
import pandas as pd
import json

st.set_page_config(page_title="–û—Ü–µ–Ω–∫–∞ –¥–∏–∞–ª–æ–≥–æ–≤", layout="wide")


def parse_transcription(transcription):
    """–ü—Ä–µ–æ–±—Ä–∞–∑—É–µ—Ç JSON-—Å—Ç—Ä–æ–∫—É –≤ —á–∏—Ç–∞–µ–º—ã–π —Ç–µ–∫—Å—Ç —Å –≤—ã–¥–µ–ª–µ–Ω–∏–µ–º –∂–∏—Ä–Ω—ã–º"""
    try:
        data = json.loads(transcription) if isinstance(transcription, str) else transcription
        lines = []
        for msg in data:
            speaker = msg.get('speaker', '')
            text = msg.get('text', '')
            if speaker == 'manager':
                lines.append(f"---–ú–µ–Ω–µ–¥–∂–µ—Ä:--- {text}")
            elif speaker.startswith('SPEAKER_') or speaker == 'UNKNOWN':
                lines.append(f"---–ö–ª–∏–µ–Ω—Ç:--- {text}")
            else:
                lines.append(f"---{speaker}:--- {text}")
        return "\n\n".join(lines)
    except:
        return str(transcription)


